import React, { useEffect, useState } from 'react';
import './modal.css';

function Modal(props) {
  const [opacity, setOpacity] = useState(0)

  useEffect(() => {
    setOpacity(1)
  }, [])

  function fadeOut() {
    setOpacity(0)
  }

  const event = document.body.addEventListener('keydown', e => {
    console.log(e)
    if(e.keyCode === 27) {
      props.close();
    }
  });

  const close = () => {
    document.body.removeEventListener(event);
    props.close();
  }

  return (
    <div autoFocus className='modal' hidden={props.hidden} onClick={close}>
      <div className='modal-body' style={{ opacity: opacity }} onClick={(e) => {
        e.stopPropagation();
      }}>
        <span className='close' onClick={() => {
          fadeOut();
          setTimeout(() => {
            close()
          }, 100)
        }}>X</span>
        <p className='modal-title'>{props.title}</p>
        {props.Content}
        <footer>
          <button className='modal-cancel' onMouseUp={close}>Cancel</button>
          <button className='modal-success' onClick={props.onSuccess}>{props.successMessage}</button>
        </footer>
      </div>
    </div>
  )
}

export default Modal
