import './todos.css';
import React, { useState, useEffect } from 'react';
import NewTodoButton from '../modals/new-todo/new-todo';
import DeleteButton from '../modals/delete-todo/delete-todo';

function Todo(props) {
  const success = async () => {
    // Add TODO's name validation
    //
    const input = document.querySelector('.modal-input');
    await fetch('/todos/create', {
      method: 'DELETE'
    });
    await props.update()
    props.modal(null);
  };

  return (
    <div className='todo' onClick={props.onClick}>
      <div key={props.id} className='todo-options'>
        <p onClick={(e) => {
          e.stopPropagation();
          props.modal(<Modal 
            key='new-todo-modal'
            opacity={opacity} 
            close={() => props.modal(null)} 
            title='New TODO'
            onSuccess={success}
            successMessage='Confirm'
            Content={<h1>Are you sure?</h1>}
          />)
        }}>...</p>
      </div>
      <div className='todo-header'>
        <h1>{props.title}</h1>
          <div className='bar'>
            <progress 
              value={props.completed} 
              max={props.tasks.length} 
              className='progress-bar'
              style={{display: props.empty ? 'none' : 'auto'}}
              >
            </progress>
          </div>
      </div>
      <div className='todo-body'>
        <div className='todo-info todo-progress'><p>{props.progress}</p></div>
        <div className='todo-info todo-date'></div>
        <div className='todo-info todo-type'></div>
      </div>
    </div>
  )
}

function Todos(props) {
  const [todos, setTodos] = useState([]);

  const fetchData = async () => {
    const todos = await fetch('/todos')
    .then((response) => {
      if(response.status === 200){
        return response.json();
      }
      throw new Error(`${response.status} - ${response.statusText}`);
    })
    setTodos(todos);
  };

  useEffect(() => {
    fetchData()
  }, []);

  async function deleteTodo(id) {

    await fetch(`/todos/delete/${id}`, {
      method: 'DELETE',
    });

    fetchData()
  }

  if (window.location.pathname === '/') {
    return [
      <NewTodoButton key='newtodo' modal={props.modal} update={fetchData}/>,
      <div key="todos" className='container'>
        <header>
          <h1>My TODOs</h1>
        </header>
        <div id="grid">
          {todos.filter(props.selectedFilter).map(todo => {
            const completed = todo.tasks.reduce((acc, curr) => {
              return curr.completed ? (acc + 1) : acc;
            }, 0);

            const empty = todo.tasks.length === 0 ? true : false;

            return <Todo 
              options={
                <DeleteButton 
                  delete={(e) => {
                    deleteTodo(todo.id);
                  }}
                  title={todo.title}/>
              }
              onClick={e => (window.location.href = `/edit/?${todo.id}`)}
              key={todo.id}
              id={todo.id} 
              empty={empty}
              title={todo.title} 
              tasks={todo.tasks} 
              completed={completed}
              progress={empty ? 'No tasks' : `${completed}/${todo.tasks.length} Tasks`} 
            />
          })}
        </div>
      </div>
    ]
  }
}

export default Todos
