import './App.css';
import { useState } from 'react';

import Todos from './todos/todos';
import Sidebar from './sidebar/sidebar.js'
import Searchbox from './searchbox/searchbox';
import Header from './header/header';
import filters from './sidebar/filters';

function App() {
  const [filter, setFilter] = useState(() => filters.All)
  const [currModal, setModal] = useState(<></>);

  function changeFilter(selectedFilter) {
    setFilter(() => selectedFilter)
  }

  const createModal = (modal) => {
    setModal(modal)
  }

  const Modal = currModal;

  return [
    <Modal/>,
    <Header key="header"/>,
    <Searchbox key='searchbox'/>,
    <Sidebar key='sidebar' filters={filters} filterChange={changeFilter}/>,
    <Todos key='todos' modal={createModal} selectedFilter={filter}/>
  ]
}

export default App;
