const completed = (tasks) => tasks.reduce((acc, curr) => curr.completed ? acc + 1 : acc, 0);

const filters = {
  'All': ([id, todo]) => {
    console.log(id, todo)
    return todo
  },
  'Unfinished': ([id, todo]) => completed(Object.values(todo.tasks)) !== todo.tasks.length,
  'Empty': ([id, todo]) => !todo.tasks.length,
  'Completed': ([id, todo]) => {
    const tasks = Object.values(todo.tasks)
    return (completed(tasks) === tasks.length) && (completed(tasks) > 0)
  }
}

export default filters
