const completed = (todo) => todo.tasks.reduce((acc, curr) => curr.completed ? acc + 1 : acc, 0);

const filters = {
  'All': ([id, todo]) => todo,
  'Unfinished': ([id, todo]) => completed(todo) !== todo.tasks.length,
  'Empty': ([id, todo]) => !todo.tasks.length,
  'Completed': ([id, todo]) => (completed(todo) === todo.tasks.length) && (completed(todo) > 0)
}

export default filters
