import React from "react";
import { Link } from "react-router-dom";
import Searchbox from "../searchbox/searchbox";
import './edit.css';
import { useState, useEffect } from "react";

export default function Edit() {
  const [currModal, setModal] = useState(null);
  const [todo, setTodo] = useState([]);
  const [tasks, setTasks] = useState([]);

  const id = window.location.search.slice(1)

  const fetchData = async () => {
    const todo = await fetch(`/todos/${id}`)
    .then((response) => {
      if(response.status === 200){
        return response.json();
      }
      throw new Error(`${response.status} - ${response.statusText}`);
    })
    setTodo(todo);
    setTasks(todo.tasks);
  };

  useEffect(() => {
    fetchData()
  }, []);

  function updateModal(modal) {
    setModal(modal);
  };

  return (
    <>
      <Link to='/' draggable='false'>
        <div id="go-back"><i className="fa-solid fa-arrow-left"></i></div>
      </Link>
      <Searchbox key='searchbox'/>
      <div id="todo-edit">
        <h1>{todo.title}</h1>
        {tasks.map((task) => {
          return (
            <div key={task.title} className=''>
              <h1>{task.title}</h1>
            </div>
          )
        })}
      </div>
    </>
  )
}
