import { useState } from 'react';
import Modal from '../modal-template';
import './new-todo.css'

function DeleteButton(props) {
  const [hideModal, setHideModal] = useState(true);
  const [opacity, setOpacity] = useState(1);

  const showModal = () => {
    setHideModal(false);
    setTimeout(() => {
      setOpacity(1)
    }, 0);
  }

  const closeModal = () => {
    setOpacity(0)
    setTimeout(() => {
      setHideModal(true);
    }, 100);
  }

  if (window.location.pathname === '/') {
    return [
      <div key={props.id} className='todo-options'>
        <p onClick={props.delete}>...</p>
      </div>,
      <Modal 
        key='delete-todo-modal'
        opacity={opacity}
        hidden={hideModal}
        close={closeModal}
        title={`Delete ${props.title} TODO`}
        onSuccess={props.delete}
        successMessage='Confirm'
        Content={<h1>{`Are you sure?`}</h1>}
      />
    ]
  }
}

export default DeleteButton
