import { useState } from 'react';
import Modal from '../modal-template';
import './new-todo.css'

function NewTodoButton(props) {
  const [modal, setModal] = useState();
  const [opacity, setOpacity] = useState(1);

  const showModal = () => {
    setModal(<Modal opacity={opacity}/>)
    setTimeout(() => {
      setOpacity(1)
    }, 0);
  }

  const closeModal = () => {
    setOpacity(0)
    setTimeout(() => {
      setModal();
    }, 100);
  }

  const success = async () => {
    // Add TODO's name validation
    //
    const input = document.querySelector('.modal-input');
    await fetch('/todos/create', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ title: input.value })
    });
    await props.update()
    closeModal();
  }

  if (window.location.pathname === '/') {
    return [
      <div key='new-todo-button' className='new-todo' onClick={showModal}>
        <p>+</p>
      </div>,
      <Modal 
        key='new-todo-modal'
        opacity={opacity}
        hidden={hideModal}
        close={closeModal}
        title='New TODO'
        onSuccess={success}
        successMessage='Create TODO'
        Content={<input placeholder="TODO's name..." type='text' className='modal-input'/>}
      />
    ]
  }
}

export default NewTodoButton
